import { Component, OnInit } from '@angular/core';
import { Router, NavigationEnd, Event } from '@angular/router';
import { AuthService } from './services/auth.service';
import { filter } from 'rxjs/operators';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'AI Central';

  constructor(
    private authService: AuthService,
    private router: Router
  ) {}

  ngOnInit() {
    // Handle browser back/forward navigation
    this.router.events.pipe(
      filter((event: Event): event is NavigationEnd => event instanceof NavigationEnd)
    ).subscribe((event: NavigationEnd) => {
      this.handleNavigation(event.url);
    });
  }

  private handleNavigation(url: string) {
    const isLoggedIn = this.authService.isLoggedIn();

    // If user is logged in and tries to access login/register pages, redirect to dashboard
    if (isLoggedIn && (url === '/login' || url === '/register')) {
      this.router.navigate(['/dashboard']);
      return;
    }

    // If user is not logged in and tries to access protected pages, redirect to login
    if (!isLoggedIn && this.isProtectedRoute(url)) {
      this.router.navigate(['/login']);
      return;
    }
  }

  private isProtectedRoute(url: string): boolean {
    const protectedRoutes = ['/dashboard', '/profile', '/user-list', '/user-create', '/user-edit', '/user-view', '/change-password'];
    return protectedRoutes.some(route => url.startsWith(route));
  }

  isLoggedIn(): boolean {
    return this.authService.isLoggedIn();
  }

  isOnLandingPage(): boolean {
    return this.router.url === '/' || this.router.url === '/login' || this.router.url === '/register';
  }

  shouldShowLandingPage(): boolean {
    return !this.isLoggedIn() && (this.router.url === '/' || this.router.url === '/login' || this.router.url === '/register');
  }

  logout(): void {
    const userData = this.authService.getUserData();
    if (userData) {
      this.authService.logout(userData.username).subscribe({
        next: () => {
          this.authService.clearUserData();
          this.router.navigate(['/login']);
        },
        error: () => {
          this.authService.clearUserData();
          this.router.navigate(['/login']);
        }
      });
    } else {
      this.authService.clearUserData();
      this.router.navigate(['/login']);
    }
  }
}
