import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../../services/auth.service';
import { UserService } from '../../services/user.service';
import { User } from '../../models/user.model';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit {
  user: User | null = null;
  loading = false;
  errorMessage = '';

  constructor(
    private authService: AuthService,
    private userService: UserService,
    private router: Router
  ) {}

  ngOnInit(): void {
    console.log('ProfileComponent initialized');
    this.loadUserProfile();
  }

  loadUserProfile(): void {
    const userData = this.authService.getUserData();
    if (!userData || !userData.email) {
      this.errorMessage = 'User data not found. Please login again.';
      return;
    }

    console.log('Loading profile for user:', userData.email);
    this.loading = true;

    // Get user by email since we have the email from auth data
    this.userService.getUserByEmail(userData.email).subscribe({
      next: (response) => {
        console.log('Profile data received:', response);
        if (response.success && response.data) {
          this.user = response.data;
        } else {
          this.errorMessage = response.message || 'Failed to load profile';
        }
        this.loading = false;
      },
      error: (error) => {
        console.error('Error loading profile:', error);
        this.errorMessage = error.error?.message || 'Failed to load profile';
        this.loading = false;
      }
    });
  }

  onEdit(): void {
    if (this.user?.id) {
      this.router.navigate(['/users', this.user.id, 'edit']);
    }
  }

  onBack(): void {
    this.router.navigate(['/users']);
  }

  logout(): void {
    const userData = this.authService.getUserData();
    if (userData?.username) {
      this.authService.logout(userData.username).subscribe({
        next: () => {
          this.authService.clearUserData();
          this.router.navigate(['/login']);
        },
        error: (error) => {
          console.error('Logout error:', error);
          this.authService.clearUserData();
          this.router.navigate(['/login']);
        }
      });
    } else {
      this.authService.clearUserData();
      this.router.navigate(['/login']);
    }
  }
}
