export class ChatComponent {
  constructor(authService, aiChatService) {
    this.authService = authService;
    this.aiChatService = aiChatService;
    this.messages = [];
    this.newMessage = '';
    this.userData = null;
    this.isTyping = false;
    this.isListening = false;
    this.isSpeaking = false;
    this.recognition = null;
    this.synthesis = null;
    this.currentConversationId = null;
    this.isLoading = false;
    // Voice customization settings
    this.voiceSettings = {
      rate: 0.9,
      pitch: 1.05,
      volume: 0.95,
      voiceName: '',
      language: 'hi-IN',
      gender: 'male' // Gender preference (male/female)
    };

    this.availableVoices = [];
    this.showVoiceSettings = false;
    // Make Math available in template
    this.Math = Math;
  }
  ngOnInit() {
    this.userData = this.authService.getUserData();
    this.addWelcomeMessage();
    this.initializeSpeech();
    // Load voices when they become available
    if (this.synthesis) {
      this.synthesis.onvoiceschanged = () => {
        this.availableVoices = this.synthesis?.getVoices() || [];
        console.log('Voices loaded:', this.availableVoices.length);
        this.loadVoiceSettings();
        // Debug: Show all available voices
        this.debugAllVoices();
      };
    }
  }
  ngAfterViewChecked() {
    this.scrollToBottom();
  }
  addWelcomeMessage() {
    this.messages.push({
      id: 1,
      text: `Namaste ${this.userData?.firstName || this.userData?.username}! Main aapka AI assistant hoon. Aapko kya help chahiye aaj?`,
      sender: 'ai',
      timestamp: new Date()
    });
  }
  sendMessage() {
    if (this.newMessage.trim() && !this.isTyping && !this.isLoading) {
      const userMessage = {
        id: this.messages.length + 1,
        text: this.newMessage.trim(),
        sender: 'user',
        timestamp: new Date()
      };
      this.messages.push(userMessage);
      const userInput = this.newMessage;
      this.newMessage = '';
      this.isTyping = true;
      this.isLoading = true;
      // Add loading message
      const loadingMessage = {
        id: this.messages.length + 1,
        text: 'Thinking...',
        sender: 'ai',
        timestamp: new Date(),
        isLoading: true
      };
      this.messages.push(loadingMessage);
      // Send to AI backend
      const request = {
        question: userInput,
        userId: this.userData?.id?.toString() || this.userData?.username,
        conversationId: this.currentConversationId || undefined
      };
      this.aiChatService.sendQuestion(request).subscribe({
        next: response => {
          // Remove loading message
          this.messages = this.messages.filter(msg => !msg.isLoading);
          // Store conversation ID for future messages
          this.currentConversationId = response.conversationId?.toString() || null;
          // Add AI response
          const aiMessage = {
            id: this.messages.length + 1,
            text: response.response,
            sender: 'ai',
            timestamp: new Date(),
            aiResponseId: response.conversationId?.toString() || '',
            conversationId: response.conversationId?.toString() || ''
          };
          this.messages.push(aiMessage);
          this.isTyping = false;
          this.isLoading = false;
        },
        error: error => {
          console.error('Error getting AI response:', error);
          // Remove loading message
          this.messages = this.messages.filter(msg => !msg.isLoading);
          // Add error message
          const errorMessage = {
            id: this.messages.length + 1,
            text: 'Sorry, main abhi aapki help nahi kar pa raha hoon. Please try again later.',
            sender: 'ai',
            timestamp: new Date()
          };
          this.messages.push(errorMessage);
          this.isTyping = false;
          this.isLoading = false;
        }
      });
    }
  }
  onKeyPress(event) {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      this.sendMessage();
    }
  }
  scrollToBottom() {
    try {
      this.chatContainer.nativeElement.scrollTop = this.chatContainer.nativeElement.scrollHeight;
    } catch (err) {}
  }
  clearChat() {
    this.messages = [];
    this.currentConversationId = null;
    this.addWelcomeMessage();
  }
  focusInput() {
    setTimeout(() => {
      this.messageInput.nativeElement.focus();
    }, 100);
  }
  submitFeedback(questionId, conversationId, rating, feedback) {
    const feedbackRequest = {
      conversationId: conversationId,
      questionId: questionId,
      rating: rating,
      feedback: feedback
    };
    this.aiChatService.submitFeedback(feedbackRequest).subscribe({
      next: response => {
        console.log('Feedback submitted successfully:', response);
        // Show a brief success message
        this.showFeedbackMessage('Thank you for your feedback!');
      },
      error: error => {
        console.error('Error submitting feedback:', error);
        this.showFeedbackMessage('Failed to submit feedback. Please try again.');
      }
    });
  }
  showFeedbackMessage(message) {
    // Create a temporary feedback message
    const feedbackMessage = {
      id: this.messages.length + 1,
      text: message,
      sender: 'ai',
      timestamp: new Date()
    };
    this.messages.push(feedbackMessage);
    // Remove the feedback message after 3 seconds
    setTimeout(() => {
      this.messages = this.messages.filter(msg => msg.id !== feedbackMessage.id);
    }, 3000);
  }
  initializeSpeech() {
    // Initialize speech synthesis
    if ('speechSynthesis' in window) {
      this.synthesis = window.speechSynthesis;
    }
    // Initialize speech recognition with enhanced settings
    if ('webkitSpeechRecognition' in window) {
      this.recognition = new window.webkitSpeechRecognition();
      // Enhanced recognition settings for better accuracy
      this.recognition.continuous = false;
      this.recognition.interimResults = true; // Get interim results for better feedback
      this.recognition.lang = 'hi-IN'; // Hindi (India)
      this.recognition.maxAlternatives = 5; // Get more alternatives for better accuracy
      // Additional settings for better performance
      this.recognition.grammars = null; // Allow any speech
      this.recognition.serviceURI = ''; // Use default service
      this.recognition.onstart = () => {
        this.isListening = true;
        console.log('Speech recognition started');
      };
      this.recognition.onresult = event => {
        let finalTranscript = '';
        let interimTranscript = '';
        // Process all results
        for (let i = event.resultIndex; i < event.results.length; i++) {
          const transcript = event.results[i][0].transcript;
          if (event.results[i].isFinal) {
            finalTranscript += transcript;
          } else {
            interimTranscript += transcript;
          }
        }
        // Show interim results for better user feedback
        if (interimTranscript) {
          this.newMessage = this.convertHindiToEnglish(interimTranscript);
        }
        // When we have final results, process them
        if (finalTranscript) {
          const englishText = this.convertHindiToEnglish(finalTranscript);
          this.newMessage = englishText;
          console.log('Final transcript:', englishText);
          // Auto-send the message after a short delay
          setTimeout(() => {
            if (this.newMessage.trim()) {
              this.sendMessage();
            }
          }, 500);
        }
      };
      this.recognition.onerror = event => {
        console.error('Speech recognition error:', event.error);
        this.isListening = false;
        // Show user-friendly error messages
        let errorMessage = 'Voice input error. Please try again.';
        switch (event.error) {
          case 'no-speech':
            errorMessage = 'No speech detected. Please speak clearly.';
            break;
          case 'audio-capture':
            errorMessage = 'Microphone not found. Please check your microphone.';
            break;
          case 'not-allowed':
            errorMessage = 'Microphone access denied. Please allow microphone access.';
            break;
          case 'network':
            errorMessage = 'Network error. Please check your internet connection.';
            break;
        }
        // You can show this error message to the user
        console.log(errorMessage);
      };
      this.recognition.onend = () => {
        this.isListening = false;
        console.log('Speech recognition ended');
      };
      this.recognition.onaudiostart = () => {
        console.log('Audio capturing started');
