import { Component, OnInit, ViewChild, ElementRef, AfterViewChecked } from '@angular/core';
import { AuthService } from '../../services/auth.service';
import { AiChatService, AiChatRequest, AiChatResponse } from '../../services/ai-chat.service';

interface ChatMessage {
  id: number;
  text: string;
  sender: 'user' | 'ai';
  timestamp: Date;
  isLoading?: boolean;
  aiResponseId?: string;
  conversationId?: string;
}

@Component({
  selector: 'app-chat',
  templateUrl: './chat.component.html',
  styleUrls: ['./chat.component.css']
})
export class ChatComponent implements OnInit, AfterViewChecked {
  @ViewChild('chatContainer') private chatContainer!: ElementRef;
  @ViewChild('messageInput') private messageInput!: ElementRef;

  messages: ChatMessage[] = [];
  newMessage: string = '';
  userData: any = null;
  isTyping: boolean = false;
  isListening: boolean = false;
  isSpeaking: boolean = false;
  recognition: any = null;
  synthesis: SpeechSynthesis | null = null;
  currentConversationId: string | null = null;
  isLoading: boolean = false;

  // Voice customization settings
  voiceSettings: {
    rate: number;
    pitch: number;
    volume: number;
    voiceName: string;
    language: string;
    gender: string;
  } = {
    rate: 0.9,           // Speech rate (0.1 to 10)
    pitch: 1.05,         // Pitch (0 to 2)
    volume: 0.95,        // Volume (0 to 1)
    voiceName: '',       // Selected voice name
    language: 'hi-IN',   // Force Hindi (India) language
    gender: 'male'       // Gender preference (male/female)
  };

  availableVoices: SpeechSynthesisVoice[] = [];
  showVoiceSettings: boolean = false;

  // Make Math available in template
  Math = Math;

  constructor(
    private authService: AuthService,
    private aiChatService: AiChatService
  ) {}

  ngOnInit(): void {
    this.userData = this.authService.getUserData();
    this.addWelcomeMessage();
    this.initializeSpeech();

    // Load voices when they become available
    if (this.synthesis) {
      this.synthesis.onvoiceschanged = () => {
        this.availableVoices = this.synthesis?.getVoices() || [];
        console.log('Voices loaded:', this.availableVoices.length);
        this.loadVoiceSettings();

        // Debug: Show all available voices
        this.debugAllVoices();
      };
    }
  }

  ngAfterViewChecked(): void {
    this.scrollToBottom();
  }

  private addWelcomeMessage(): void {
    this.messages.push({
      id: 1,
      text: `Namaste ${this.userData?.firstName || this.userData?.username}! Main aapka AI assistant hoon. Aapko kya help chahiye aaj?`,
      sender: 'ai',
      timestamp: new Date()
    });
  }

  sendMessage(): void {
    if (this.newMessage.trim() && !this.isTyping && !this.isLoading) {
      const userMessage: ChatMessage = {
        id: this.messages.length + 1,
        text: this.newMessage.trim(),
        sender: 'user',
        timestamp: new Date()
      };

      this.messages.push(userMessage);
      const userInput = this.newMessage;
      this.newMessage = '';
      this.isTyping = true;
      this.isLoading = true;

      // Add loading message
      const loadingMessage: ChatMessage = {
        id: this.messages.length + 1,
        text: 'Thinking...',
        sender: 'ai',
        timestamp: new Date(),
        isLoading: true
      };
      this.messages.push(loadingMessage);

      // Send to AI backend
      const request: AiChatRequest = {
        question: userInput,
        userId: this.userData?.id?.toString() || this.userData?.username,
        conversationId: this.currentConversationId || undefined
      };

      this.aiChatService.sendQuestion(request).subscribe({
        next: (response: AiChatResponse) => {
          // Remove loading message
          this.messages = this.messages.filter(msg => !msg.isLoading);

          // Store conversation ID for future messages
          this.currentConversationId = response.conversationId?.toString() || null;

          // Add AI response
          const aiMessage: ChatMessage = {
            id: this.messages.length + 1,
            text: response.response,
            sender: 'ai',
            timestamp: new Date(),
            aiResponseId: response.conversationId?.toString() || '',
            conversationId: response.conversationId?.toString() || ''
          };

          this.messages.push(aiMessage);
          this.isTyping = false;
          this.isLoading = false;
        },
        error: (error) => {
          console.error('Error getting AI response:', error);

          // Remove loading message
          this.messages = this.messages.filter(msg => !msg.isLoading);

          // Add error message
          const errorMessage: ChatMessage = {
            id: this.messages.length + 1,
            text: 'Sorry, main abhi aapki help nahi kar pa raha hoon. Please try again later.',
            sender: 'ai',
            timestamp: new Date()
          };

          this.messages.push(errorMessage);
          this.isTyping = false;
          this.isLoading = false;
        }
      });
    }
  }

  onKeyPress(event: KeyboardEvent): void {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      this.sendMessage();
    }
  }

  private scrollToBottom(): void {
    try {
      this.chatContainer.nativeElement.scrollTop = this.chatContainer.nativeElement.scrollHeight;
    } catch (err) {}
  }

  clearChat(): void {
    this.messages = [];
    this.currentConversationId = null;
    this.addWelcomeMessage();
  }

  focusInput(): void {
    setTimeout(() => {
      this.messageInput.nativeElement.focus();
    }, 100);
  }

  submitFeedback(questionId: string, conversationId: string, rating: number, feedback: string): void {
    const feedbackRequest = {
      conversationId: conversationId,
      questionId: questionId,
      rating: rating,
      feedback: feedback
    };

    this.aiChatService.submitFeedback(feedbackRequest).subscribe({
      next: (response) => {
        console.log('Feedback submitted successfully:', response);
        // Show a brief success message
        this.showFeedbackMessage('Thank you for your feedback!');
      },
      error: (error) => {
        console.error('Error submitting feedback:', error);
        this.showFeedbackMessage('Failed to submit feedback. Please try again.');
      }
    });
  }

  private showFeedbackMessage(message: string): void {
    // Create a temporary feedback message
    const feedbackMessage: ChatMessage = {
      id: this.messages.length + 1,
      text: message,
      sender: 'ai',
      timestamp: new Date()
    };

    this.messages.push(feedbackMessage);

    // Remove the feedback message after 3 seconds
    setTimeout(() => {
      this.messages = this.messages.filter(msg => msg.id !== feedbackMessage.id);
    }, 3000);
  }

  private initializeSpeech(): void {
    // Initialize speech synthesis
    if ('speechSynthesis' in window) {
      this.synthesis = window.speechSynthesis;
    }

    // Initialize speech recognition with enhanced settings
    if ('webkitSpeechRecognition' in window) {
      this.recognition = new (window as any).webkitSpeechRecognition();

      // Enhanced recognition settings for better accuracy
      this.recognition.continuous = false;
      this.recognition.interimResults = true; // Get interim results for better feedback
      this.recognition.lang = 'hi-IN'; // Hindi (India)
      this.recognition.maxAlternatives = 5; // Get more alternatives for better accuracy

      // Additional settings for better performance
      this.recognition.grammars = null; // Allow any speech
      this.recognition.serviceURI = ''; // Use default service

      this.recognition.onstart = () => {
        this.isListening = true;
        console.log('Speech recognition started');
      };

      this.recognition.onresult = (event: any) => {
        let finalTranscript = '';
        let interimTranscript = '';

        // Process all results
        for (let i = event.resultIndex; i < event.results.length; i++) {
          const transcript = event.results[i][0].transcript;
          if (event.results[i].isFinal) {
            finalTranscript += transcript;
          } else {
            interimTranscript += transcript;
          }
        }

        // Show interim results for better user feedback
        if (interimTranscript) {
          this.newMessage = this.convertHindiToEnglish(interimTranscript);
        }

        // When we have final results, process them
        if (finalTranscript) {
          const englishText = this.convertHindiToEnglish(finalTranscript);
          this.newMessage = englishText;
          console.log('Final transcript:', englishText);

          // Auto-send the message after a short delay
          setTimeout(() => {
            if (this.newMessage.trim()) {
              this.sendMessage();
            }
          }, 500);
        }
      };

      this.recognition.onerror = (event: any) => {
        console.error('Speech recognition error:', event.error);
        this.isListening = false;

        // Show user-friendly error messages
        let errorMessage = 'Voice input error. Please try again.';
        switch (event.error) {
          case 'no-speech':
            errorMessage = 'No speech detected. Please speak clearly.';
            break;
          case 'audio-capture':
            errorMessage = 'Microphone not found. Please check your microphone.';
            break;
          case 'not-allowed':
            errorMessage = 'Microphone access denied. Please allow microphone access.';
            break;
          case 'network':
            errorMessage = 'Network error. Please check your internet connection.';
            break;
        }

        // You can show this error message to the user
        console.log(errorMessage);
      };

      this.recognition.onend = () => {
        this.isListening = false;
        console.log('Speech recognition ended');
      };

      this.recognition.onaudiostart = () => {
        console.log('Audio capturing started');
      };

      this.recognition.onaudioend = () => {
        console.log('Audio capturing ended');
      };

      this.recognition.onsoundstart = () => {
        console.log('Sound detected');
      };

      this.recognition.onsoundend = () => {
        console.log('Sound ended');
      };

      this.recognition.onspeechstart = () => {
        console.log('Speech started');
      };

      this.recognition.onspeechend = () => {
        console.log('Speech ended');
      };
    } else {
      console.error('Speech recognition not supported in this browser');
    }
  }

  startListening(): void {
    if (this.recognition && !this.isListening) {
      try {
        this.recognition.start();
      } catch (error) {
        console.error('Error starting speech recognition:', error);
        this.isListening = false;
      }
    }
  }

  stopListening(): void {
    if (this.recognition && this.isListening) {
      try {
        this.recognition.stop();
      } catch (error) {
        console.error('Error stopping speech recognition:', error);
      }
    }
  }

  speakMessage(text: string): void {
    if (this.synthesis) {
      try {
        // Stop any current speech
        this.synthesis.cancel();

        // Process text for more natural speech
        const processedText = this.processTextForSpeech(text);

        const utterance = new SpeechSynthesisUtterance(processedText);

        // Enhanced voice selection based on gender preference
        const voices = this.synthesis.getVoices();
        const preferredVoices = this.getPreferredVoicesByGender();

        // Find the best available voice with enhanced selection
        let selectedVoice = this.selectBestVoice(voices, preferredVoices);

        if (selectedVoice) {
          utterance.voice = selectedVoice;
          console.log('Selected voice:', selectedVoice.name, 'Language:', selectedVoice.lang, 'Gender preference:', this.voiceSettings.gender);
          console.log('Is male voice:', this.isMaleVoice(selectedVoice));
          console.log('Is female voice:', this.isFemaleVoice(selectedVoice));
        } else {
          console.warn('No suitable voice found for gender preference:', this.voiceSettings.gender);
          // Log available voices for debugging
          console.log('Available voices:');
          voices.forEach(voice => {
            console.log(`- ${voice.name} (${voice.lang}) - Male: ${this.isMaleVoice(voice)}, Female: ${this.isFemaleVoice(voice)}`);
          });
        }

        // Apply user voice settings for personalized AI voice
        utterance.rate = this.voiceSettings.rate;
        utterance.pitch = this.voiceSettings.pitch;
        utterance.volume = this.voiceSettings.volume;
        // Always use Hindi language for male voice consistency
        utterance.lang = 'hi-IN';

        // Add natural pauses and emphasis
        this.addSpeechEvents(utterance);

        this.synthesis.speak(utterance);
      } catch (error) {
        console.error('Error with speech synthesis:', error);
        this.isSpeaking = false;
      }
    }
  }

  private processTextForSpeech(text: string): string {
    // Add natural pauses and emphasis for more genuine speech
    let processedText = text;

    // Add pauses after sentences
    processedText = processedText.replace(/\./g, '... ');
    processedText = processedText.replace(/\!/g, '!... ');
    processedText = processedText.replace(/\?/g, '?... ');

    // Add emphasis to important words
    processedText = processedText.replace(/\b(Ayush Dwivedi|Alka Dwivedi|Anshi Shukla|Vijay Kumar Dwivedi|Shreya Dwivedi)\b/gi, 
      '<emphasis level="strong">$1</emphasis>');

    // Add natural pauses for better flow
    processedText = processedText.replace(/,/g, ',... ');
    processedText = processedText.replace(/;/g, ';... ');

    // Optimize for Hindi-English mixed speech
    if (this.containsHindi(text)) {
      processedText = this.optimizeHindiEnglishSpeech(processedText);
    }

    return processedText;
  }

  private selectBestVoice(voices: SpeechSynthesisVoice[], preferredVoices: string[]): SpeechSynthesisVoice | null {
    // First, try to use user's selected voice (if it matches gender preference)
    if (this.voiceSettings.voiceName) {
      const userVoice = voices.find(v => v.name === this.voiceSettings.voiceName);
      if (userVoice && this.matchesGenderPreference(userVoice)) {
        return userVoice;
      }
    }

    // Then try to find exact matches from preferred voices
    for (const voiceName of preferredVoices) {
      const voice = voices.find(v => v.name === voiceName);
      if (voice && this.matchesGenderPreference(voice)) {
        return voice;
      }
    }

    // If no exact match, find best available Hindi voice matching gender preference
    const hindiVoice = voices.find(v => 
      v.lang.startsWith('hi-IN') && this.matchesGenderPreference(v)
    );

    if (hindiVoice) {
      return hindiVoice;
    }

    // If no Hindi voice available, try Indian English voice matching gender preference
    const indianEnglishVoice = voices.find(v => 
      v.lang.startsWith('en-IN') && this.matchesGenderPreference(v)
    );

    if (indianEnglishVoice) {
      return indianEnglishVoice;
    }

    // Last resort: any Hindi voice (but log a warning)
    const anyHindiVoice = voices.find(v => v.lang.startsWith('hi-IN'));
    if (anyHindiVoice) {
      console.warn(`No ${this.voiceSettings.gender} Hindi voice found, using available Hindi voice:`, anyHindiVoice.name);
      return anyHindiVoice;
    }

    // Final fallback: any available voice (but log a warning)
    if (voices.length > 0) {
      console.warn('No Hindi voice found, using fallback voice:', voices[0].name);
      return voices[0];
    }

    return null;
  }

  private isMaleVoice(voice: SpeechSynthesisVoice): boolean {
    const voiceName = voice.name.toLowerCase();

    // Explicit male voice indicators
    const maleIndicators = [
      'male', 'madhur', 'prabhat', 'guy', 'ryan', 'davis', 'alfie', 
      'david', 'mark', 'james', 'john', 'mike', 'tom', 'alex'
    ];

    // Explicit female voice indicators
    const femaleIndicators = [
      'female', 'neerja', 'jenny', 'sonia', 'sarah', 'lisa', 'emma', 
      'anna', 'maria', 'sophie', 'lisa', 'kate', 'jane', 'mary'
    ];

    // Check for explicit male indicators
    const hasMaleIndicator = maleIndicators.some(indicator => voiceName.includes(indicator));

    // Check for explicit female indicators
    const hasFemaleIndicator = femaleIndicators.some(indicator => voiceName.includes(indicator));

    // If we have explicit indicators, use them
    if (hasMaleIndicator && !hasFemaleIndicator) {
      return true;
    }
    if (hasFemaleIndicator && !hasMaleIndicator) {
      return false;
    }

    // For voices without clear indicators, use pitch analysis
    // Male voices typically have lower pitch values
    if (voice.name.includes('Neural')) {
      // For neural voices, rely on name patterns
      return !hasFemaleIndicator;
    }

    // Default to checking if it's NOT explicitly female
    return !hasFemaleIndicator;
  }

  private isFemaleVoice(voice: SpeechSynthesisVoice): boolean {
    const voiceName = voice.name.toLowerCase();

    // Explicit female voice indicators
    const femaleIndicators = [
      'female', 'neerja', 'jenny', 'sonia', 'sarah', 'lisa', 'emma', 
      'anna', 'maria', 'sophie', 'kate', 'jane', 'mary', 'alice'
    ];

    // Explicit male voice indicators
    const maleIndicators = [
      'male', 'madhur', 'prabhat', 'guy', 'ryan', 'davis', 'alfie', 
      'david', 'mark', 'james', 'john', 'mike', 'tom', 'alex'
    ];

    // Check for explicit female indicators
    const hasFemaleIndicator = femaleIndicators.some(indicator => voiceName.includes(indicator));

    // Check for explicit male indicators
    const hasMaleIndicator = maleIndicators.some(indicator => voiceName.includes(indicator));

    // If we have explicit indicators, use them
    if (hasFemaleIndicator && !hasMaleIndicator) {
      return true;
    }
    if (hasMaleIndicator && !hasFemaleIndicator) {
      return false;
    }

    // For voices without clear indicators, use name patterns
    if (voice.name.includes('Neural')) {
      // For neural voices, rely on name patterns
      return hasFemaleIndicator;
    }

    // Default to checking if it's explicitly female
    return hasFemaleIndicator;
  }

  private isMaleHindiVoice(voice: SpeechSynthesisVoice): boolean {
    return voice.lang.startsWith('hi-IN') && this.isMaleVoice(voice);
  }

  private matchesGenderPreference(voice: SpeechSynthesisVoice): boolean {
    if (this.voiceSettings.gender === 'male') {
      return this.isMaleVoice(voice);
    } else {
      return this.isFemaleVoice(voice);
    }
  }

  private getPreferredVoicesByGender(): string[] {
    if (this.voiceSettings.gender === 'male') {
      return [
        // Hindi Male voices - in order of preference
        'hi-IN-MadhurNeural',      // Hindi Male Neural - Most natural male voice
        'hi-IN-Madhur',            // Hindi Male (non-neural fallback)
        'hi-IN',                   // Hindi (will find male if available)

        // Only if no Hindi male voice is available, fallback to Indian English male
        'en-IN-PrabhatNeural',     // English (India) Male Neural
        'en-IN-Prabhat',           // English (India) Male (non-neural fallback)
        'en-IN'                    // English (India) (will find male if available)
      ];
    } else {
      return [
        // Hindi Female voices - in order of preference
        'hi-IN-NeerjaNeural',      // Hindi Female Neural - Most natural female voice
        'hi-IN-Neerja',            // Hindi Female (non-neural fallback)
        'hi-IN',                   // Hindi (will find female if available)

        // Only if no Hindi female voice is available, fallback to Indian English female
        'en-IN-NeerjaNeural',      // English (India) Female Neural
        'en-IN-Neerja',            // English (India) Female (non-neural fallback)
        'en-IN'                    // English (India) (will find female if available)
      ];
    }
  }

  private detectLanguage(text: string): string {
    // Always prefer Hindi for male voice
    const hindiWords = ['है', 'में', 'आप', 'मुझे', 'क्या', 'कैसे', 'कहाँ', 'कब', 'कौन', 'मदद', 'समस्या', 'समाधान'];
    const hasHindi = hindiWords.some(word => text.includes(word));

    // Always return Hindi if Hindi words are detected, otherwise use Hindi as default
    if (hasHindi) {
      return 'hi-IN';
    } else {
      // Even for English text, prefer Hindi voice for consistency
      return 'hi-IN';
    }
  }

  private containsHindi(text: string): boolean {
    const hindiPattern = /[\u0900-\u097F]/;
    return hindiPattern.test(text);
  }

  private optimizeHindiEnglishSpeech(text: string): string {
    // Add natural pauses between Hindi and English words
    text = text.replace(/([a-zA-Z]+)([हैमेंआपमुझेक्याकैसेकहाँकबकौन])/g, '$1... $2');
    text = text.replace(/([हैमेंआपमुझेक्याकैसेकहाँकबकौन])([a-zA-Z]+)/g, '$1... $2');

    return text;
  }

  private addSpeechEvents(utterance: SpeechSynthesisUtterance): void {
    utterance.onstart = () => {
      this.isSpeaking = true;
      console.log('AI started speaking');
    };

    utterance.onend = () => {
      this.isSpeaking = false;
      console.log('AI finished speaking');
    };

    utterance.onpause = () => {
      console.log('AI speech paused');
    };

    utterance.onresume = () => {
      console.log('AI speech resumed');
    };

    utterance.onerror = (event) => {
      console.error('Speech synthesis error:', event);
      this.isSpeaking = false;
    };
  }

  stopSpeaking(): void {
    if (this.synthesis) {
      try {
        this.synthesis.cancel();
        this.isSpeaking = false;
      } catch (error) {
        console.error('Error stopping speech synthesis:', error);
      }
    }
  }

  toggleSpeech(): void {
    if (this.isSpeaking) {
      this.stopSpeaking();
    } else {
      // Speak the last AI message
      const lastAiMessage = this.messages
        .filter(msg => msg.sender === 'ai')
        .pop();
      if (lastAiMessage) {
        this.speakMessage(lastAiMessage.text);
      }
    }
  }

