      'चार': 'char',
      'पांच': 'paanch',
      'छह': 'cheh',
      'सात': 'saat',
      'आठ': 'aath',
      'नौ': 'nau',
      'दस': 'das',

      // Common expressions
      'धन्यवाद': 'thank you',
      'कृपया': 'please',
      'माफ़ करें': 'sorry',
      'ठीक है': 'okay',
      'हाँ': 'yes',
      'नहीं': 'no',
      'बहुत': 'bahut',
      'अच्छा': 'accha',
