      .replace(/के ऊपर/g, 'ke upar')
      .replace(/के नीचे/g, 'ke neeche')
      .replace(/के आगे/g, 'ke aage')
      .replace(/के पीछे/g, 'ke peeche')
      .replace(/के दाएं/g, 'ke daayein')
      .replace(/के बाएं/g, 'ke baayein');

    return englishText;
  }

  // Voice customization methods
  toggleVoiceSettings(): void {
    this.showVoiceSettings = !this.showVoiceSettings;
  }

  toggleVoiceGender(): void {
    this.voiceSettings.gender = this.voiceSettings.gender === 'male' ? 'female' : 'male';
    this.saveVoiceSettings();
    console.log(`Voice gender switched to: ${this.voiceSettings.gender}`);

    // Show a brief notification
    this.showFeedbackMessage(`Voice switched to ${this.voiceSettings.gender} voice`);
  }

