    this.voiceSettings.gender = gender;
    this.saveVoiceSettings();
    console.log(`Voice gender set to: ${gender}`);

    // Debug: Show available voices for this gender
    this.debugVoiceSelection();

    // Show a brief notification
    this.showFeedbackMessage(`Voice switched to ${gender} voice`);
  }

