    if (this.synthesis) {
      const voices = this.synthesis.getVoices();

      // Try to find any male voice (Hindi or Indian English)
      const maleVoice = voices.find(voice => 
        this.isMaleVoice(voice) && 
        (voice.lang.startsWith('hi-IN') || voice.lang.startsWith('en-IN'))
      );

      if (maleVoice) {
        this.voiceSettings.voiceName = maleVoice.name;
