  updateVoiceSettings(setting: 'rate' | 'pitch' | 'volume' | 'voiceName' | 'language' | 'gender', event: Event): void {
    const target = event.target as HTMLInputElement | HTMLSelectElement;
    const value = setting === 'rate' || setting === 'pitch' || setting === 'volume' 
      ? parseFloat(target.value) 
      : target.value;

    (this.voiceSettings as any)[setting] = value;
    this.saveVoiceSettings();
  }

  selectVoice(event: Event): void {
    const target = event.target as HTMLSelectElement;
    const voiceName = target.value;
    this.voiceSettings.voiceName = voiceName;
    this.saveVoiceSettings();
    console.log('Voice selected:', voiceName);
  }

  private loadVoiceSettings(): void {
    const saved = localStorage.getItem('aiVoiceSettings');
    if (saved) {
      this.voiceSettings = { ...this.voiceSettings, ...JSON.parse(saved) };
    }
  }

  private saveVoiceSettings(): void {
    localStorage.setItem('aiVoiceSettings', JSON.stringify(this.voiceSettings));
  }

  getFilteredVoices(): SpeechSynthesisVoice[] {
    if (this.voiceSettings.language === 'auto') {
      return this.availableVoices;
    }
    return this.availableVoices.filter(voice => 
      voice.lang.startsWith(this.voiceSettings.language)
    );
  }

  getVoiceDisplayName(voice: SpeechSynthesisVoice): string {
    const lang = voice.lang.split('-')[0];
    const country = voice.lang.split('-')[1];
    const isNeural = voice.name.includes('Neural');

    let displayName = voice.name;
    if (isNeural) {
      displayName += ' (Neural)';
    }
    displayName += ` (${lang.toUpperCase()}-${country})`;

    return displayName;
  }
