import { Component, OnInit, ViewChild, ElementRef, AfterViewChecked } from '@angular/core';
import { AuthService } from '../../services/auth.service';

interface ChatMessage {
  id: number;
  text: string;
  sender: 'user' | 'ai';
  timestamp: Date;
  isLoading?: boolean;
}

@Component({
  selector: 'app-chat',
  templateUrl: './chat.component.html',
  styleUrls: ['./chat.component.css']
})
export class ChatComponent implements OnInit, AfterViewChecked {
  @ViewChild('chatContainer') private chatContainer!: ElementRef;
  @ViewChild('messageInput') private messageInput!: ElementRef;

  messages: ChatMessage[] = [];
  newMessage: string = '';
  userData: any = null;
  isTyping: boolean = false;
  isListening: boolean = false;
  isSpeaking: boolean = false;
  recognition: any = null;
  synthesis: SpeechSynthesis | null = null;

  constructor(private authService: AuthService) {}

  ngOnInit(): void {
    this.userData = this.authService.getUserData();
    this.addWelcomeMessage();
    this.initializeSpeech();

    // Load voices when they become available
    if (this.synthesis) {
      this.synthesis.onvoiceschanged = () => {
        console.log('Voices loaded:', this.synthesis?.getVoices().length);
      };
    }
  }

  ngAfterViewChecked(): void {
    this.scrollToBottom();
  }

  private addWelcomeMessage(): void {
    this.messages.push({
      id: 1,
      text: `Hello ${this.userData?.firstName || this.userData?.username}! I'm your AI assistant. How can I help you today?`,
      sender: 'ai',
      timestamp: new Date()
    });
  }

  sendMessage(): void {
    if (this.newMessage.trim() && !this.isTyping) {
      const userMessage: ChatMessage = {
        id: this.messages.length + 1,
        text: this.newMessage.trim(),
        sender: 'user',
        timestamp: new Date()
      };

      this.messages.push(userMessage);
      const userInput = this.newMessage;
      this.newMessage = '';
      this.isTyping = true;

      // Simulate AI response
      setTimeout(() => {
        this.generateAIResponse(userInput);
        this.isTyping = false;
      }, 1000 + Math.random() * 2000); // Random delay between 1-3 seconds
    }
  }

  private generateAIResponse(userInput: string): void {
    const responses = [
      "I understand your request. Let me help you with that.",
      "That's an interesting question! Here's what I can tell you...",
      "I'm processing your request. Here's my analysis...",
      "Based on your input, I recommend the following approach...",
      "Great question! Let me provide you with some insights...",
      "I can help you with that. Here's what you need to know...",
      "Thank you for asking. Here's my response...",
      "I've analyzed your request. Here are the key points...",
      "That's a good point. Let me elaborate on that...",
      "I'm here to assist you. Here's what I found..."
    ];

    const randomResponse = responses[Math.floor(Math.random() * responses.length)];

    const aiMessage: ChatMessage = {
      id: this.messages.length + 1,
      text: randomResponse,
      sender: 'ai',
      timestamp: new Date()
    };

    this.messages.push(aiMessage);
  }

  onKeyPress(event: KeyboardEvent): void {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      this.sendMessage();
    }
  }

  private scrollToBottom(): void {
    try {
      this.chatContainer.nativeElement.scrollTop = this.chatContainer.nativeElement.scrollHeight;
    } catch (err) {}
  }

  clearChat(): void {
    this.messages = [];
    this.addWelcomeMessage();
  }

  focusInput(): void {
    setTimeout(() => {
      this.messageInput.nativeElement.focus();
    }, 100);
  }

  private initializeSpeech(): void {
    // Initialize speech synthesis
    if ('speechSynthesis' in window) {
      this.synthesis = window.speechSynthesis;
    }

    // Initialize speech recognition
    if ('webkitSpeechRecognition' in window) {
      this.recognition = new (window as any).webkitSpeechRecognition();
      this.recognition.continuous = false;
      this.recognition.interimResults = false;
      this.recognition.lang = 'hi-IN'; // Hindi (India)
      this.recognition.maxAlternatives = 3; // Get multiple recognition alternatives

      this.recognition.onstart = () => {
        this.isListening = true;
      };

      this.recognition.onresult = (event: any) => {
        const transcript = event.results[0][0].transcript;
        this.newMessage = transcript;
        this.isListening = false;
      };

      this.recognition.onerror = (event: any) => {
        console.error('Speech recognition error:', event.error);
        this.isListening = false;
      };

      this.recognition.onend = () => {
        this.isListening = false;
      };
    }
  }

  startListening(): void {
    if (this.recognition && !this.isListening) {
      try {
        this.recognition.start();
      } catch (error) {
        console.error('Error starting speech recognition:', error);
        this.isListening = false;
      }
    }
  }

  stopListening(): void {
    if (this.recognition && this.isListening) {
      try {
        this.recognition.stop();
      } catch (error) {
        console.error('Error stopping speech recognition:', error);
      }
    }
  }

  speakMessage(text: string): void {
    if (this.synthesis) {
      try {
        // Stop any current speech
        this.synthesis.cancel();

        const utterance = new SpeechSynthesisUtterance(text);

        // Get available voices and select a clear Hindi/English voice
        const voices = this.synthesis.getVoices();
        const preferredVoices = [
          'hi-IN-NeerjaNeural', // Hindi Neural voice
          'en-IN-NeerjaNeural', // English (India) Neural voice
          'hi-IN-MadhurNeural', // Hindi Male Neural voice
          'en-IN-PrabhatNeural', // English (India) Male Neural voice
          'en-US-JennyNeural',   // Clear English voice
          'en-GB-SoniaNeural'    // British English voice
        ];

        // Find the best available voice
        let selectedVoice = null;
        for (const voiceName of preferredVoices) {
          selectedVoice = voices.find(voice => voice.name === voiceName);
          if (selectedVoice) break;
        }

        // If no preferred voice found, try to find any Hindi or Indian English voice
        if (!selectedVoice) {
          selectedVoice = voices.find(voice => 
            voice.lang.startsWith('hi-') || 
            voice.lang.startsWith('en-IN') ||
            voice.name.toLowerCase().includes('hindi') ||
            voice.name.toLowerCase().includes('india')
          );
        }

        // If still no voice found, use default
        if (selectedVoice) {
          utterance.voice = selectedVoice;
        }

        // Optimize for clear Hindi/English speech
        utterance.rate = 0.85;      // Slightly slower for clarity
        utterance.pitch = 1.1;      // Slightly higher pitch for clarity
        utterance.volume = 1.0;     // Full volume for clear audio
        utterance.lang = 'hi-IN';   // Set language to Hindi

        utterance.onstart = () => {
          this.isSpeaking = true;
        };

        utterance.onend = () => {
          this.isSpeaking = false;
        };

        utterance.onerror = (event) => {
          console.error('Speech synthesis error:', event);
          this.isSpeaking = false;
        };

        this.synthesis.speak(utterance);
      } catch (error) {
        console.error('Error with speech synthesis:', error);
        this.isSpeaking = false;
      }
    }
  }

  stopSpeaking(): void {
    if (this.synthesis) {
      try {
        this.synthesis.cancel();
        this.isSpeaking = false;
      } catch (error) {
        console.error('Error stopping speech synthesis:', error);
      }
    }
  }

  toggleSpeech(): void {
    if (this.isSpeaking) {
      this.stopSpeaking();
    } else {
      // Speak the last AI message
      const lastAiMessage = this.messages
        .filter(msg => msg.sender === 'ai')
        .pop();
      if (lastAiMessage) {
        this.speakMessage(lastAiMessage.text);
      }
    }
  }

  getAvailableVoices(): void {
    if (this.synthesis) {
      const voices = this.synthesis.getVoices();
      console.log('Available voices:');
      voices.forEach(voice => {
        console.log(`- ${voice.name} (${voice.lang})`);
      });
    }
  }
}
