import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-forget-password',
  templateUrl: './forget-password.component.html',
  styleUrls: ['./forget-password.component.css']
})
export class ForgetPasswordComponent implements OnInit {
  forgetPasswordForm: FormGroup;
  verifyOtpForm: FormGroup;
  currentStep: 'email' | 'otp' | 'password' = 'email';
  isLoading = false;
  errorMessage = '';
  successMessage = '';
  email = '';

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private router: Router
  ) {
    this.forgetPasswordForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]]
    });

    this.verifyOtpForm = this.fb.group({
      otp: ['', [Validators.required, Validators.minLength(6), Validators.maxLength(6)]],
      newPassword: ['', [Validators.required, Validators.minLength(6)]],
      confirmPassword: ['', [Validators.required]]
    }, { validators: this.passwordMatchValidator });
  }

  ngOnInit(): void {
    // If user is logged in, clear their session and redirect to login
    // This ensures users can only reset passwords for emails they can access
    if (this.authService.isLoggedIn()) {
      this.authService.clearUserData();
      this.router.navigate(['/login'], { 
        queryParams: { 
          message: 'Please log out first to reset your password. You can only reset passwords for emails you can access.' 
        } 
      });
      return;
    }

    // Ensure email field is always empty
    this.forgetPasswordForm.patchValue({ email: '' });
  }

  passwordMatchValidator(form: FormGroup) {
    const newPassword = form.get('newPassword')?.value;
    const confirmPassword = form.get('confirmPassword')?.value;
    return newPassword === confirmPassword ? null : { passwordMismatch: true };
  }

  onSendOtp() {
    if (this.forgetPasswordForm.valid) {
      this.isLoading = true;
      this.errorMessage = '';
      this.email = this.forgetPasswordForm.get('email')?.value;

      this.authService.forgetPassword({ email: this.email }).subscribe({
        next: (response) => {
          this.isLoading = false;
          if (response.success) {
            this.successMessage = 'OTP sent successfully to your email!';
            this.currentStep = 'otp';
            setTimeout(() => {
              this.successMessage = '';
            }, 3000);
          } else {
            this.errorMessage = response.message || 'Failed to send OTP';
          }
        },
        error: (error) => {
          this.isLoading = false;
          this.errorMessage = error.error?.message || 'Failed to send OTP. Please try again.';
        }
      });
    }
  }

  onVerifyOtp() {
    if (this.verifyOtpForm.valid) {
      this.isLoading = true;
      this.errorMessage = '';

      const verifyOtpRequest = {
        email: this.email,
        otp: this.verifyOtpForm.get('otp')?.value,
        newPassword: this.verifyOtpForm.get('newPassword')?.value,
        confirmPassword: this.verifyOtpForm.get('confirmPassword')?.value
      };

      this.authService.verifyOtpAndResetPassword(verifyOtpRequest).subscribe({
        next: (response) => {
          this.isLoading = false;
          if (response.success) {
            this.successMessage = 'Password reset successfully! Redirecting to login...';
            setTimeout(() => {
              this.router.navigate(['/login']);
            }, 2000);
          } else {
            this.errorMessage = response.message || 'Failed to reset password';
          }
        },
        error: (error) => {
          this.isLoading = false;
          this.errorMessage = error.error?.message || 'Failed to reset password. Please try again.';
        }
      });
    }
  }

  goBack() {
    if (this.currentStep === 'otp') {
      this.currentStep = 'email';
      this.verifyOtpForm.reset();
    }
  }

  resendOtp() {
    this.onSendOtp();
  }

  goToLogin() {
    this.router.navigate(['/login']);
  }
}
